import React, { Component } from 'react';
import { Row, Col } from 'antd';
import './index.css';

export default class List extends Component {
    render() {
        const { list } = this.props;
        return (
            <div><Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
            {list.map(item => {
                return <Col className="gutter-row" span={6}>
                            <div className='list-box'>
                                <div>
                                    <img alt='' src={item.icon} width={48}></img>
                                    <h3>{item.title}</h3>
                                    <span>{item.category}</span>
                                </div>
                                <div>
                                    <span>{item.description}</span>
                                </div>
                                <ul>
                                    {item.share.map(shareItem => {
                                        return <li><img alt='' src={shareItem.icon} width={24}></img></li>
                                    })}
                                </ul>
                            </div>
                        </Col>

            })}
            </Row>
            </div>
        )
    }
}
