import './App.css';
import React, { Component } from 'react'
import { Layout, Menu, Image } from 'antd';
import List from './components/List'

const { Header, Content, Sider } = Layout;

class App extends Component {
  state = {
    collapsed: false,
    list:{
      Wallet:[
        {
          icon:'/images/WechatIMG406.png',
          title:'Findora Wallet',
          category:'Wallet',
          description:'The Findora wallet enables you to send and receive FRA tokens and even create your own custom assets.',
          share:[
            {
              icon:'/images/Vector.png',
              link:'#'
            },
            {
              icon:'/images/Vector-1.png',
              link:'#'
            },
            {
              icon:'/images/Vector-2.png',
              link:'#'
            },
            {
              icon:'/images/Vector-3.png',
              link:'#'
            },
            {
              icon:'/images/Group 10.png',
              link:'#'
            }
          ]

        }
      ],
      Explorer:[],
      DeFi:[],
      NFT:[],
      GameFi:[]
    }
  };

  onCollapse = collapsed => {
    console.log(collapsed);
    this.setState({ collapsed });
  };
  render() {
    const { collapsed } = this.state;
    return (
      <Layout style={{ minHeight: '100vh' }}>
        <Sider collapsible collapsed={collapsed} onCollapse={this.onCollapse}>
          <div className="logo">
            <Image src='/images/logo.png' />
          </div>
          <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1">
              All
            </Menu.Item>
            <Menu.Item key="2">
              Wallet
            </Menu.Item>
            <Menu.Item key="3">
              Explorer
            </Menu.Item>
            <Menu.Item key="4">
              DeFi
            </Menu.Item>
            <Menu.Item key="5">
              NFT
            </Menu.Item>
            <Menu.Item key="6">
              GameFi
            </Menu.Item>
          </Menu>
        </Sider>
        <Layout className="site-layout">
          <Header className="site-layout-background" style={{ padding: 0 }} />
          <Content style={{ margin: '0 16px' }}>
            <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
              <List />
            </div>
          </Content>
          {/* <Footer style={{ textAlign: 'center' }}>Ant Design ©2018 Created by Ant UED</Footer> */}
        </Layout>
      </Layout>
    );
  }
}

export default App;
